_changeCursorWithAngle: function(direction, angle) {
    var flag,cursorArray = ['auto','nwse-resize','ns-resize','nesw-resize','ew-resize','nwse-resize','ns-resize','nesw-resize','ew-resize'];
    switch(direction)
    {
        case 'northwest':
        flag = 1;
        break;
        case 'north':
        flag = 2
        break;
        case 'northeast':
        flag = 3
        break;
        case 'east':
        flag = 4;
        break;
        case 'southeast':
        flag = 5;
        break;
        case 'south':
        flag = 6;
        break;
        case 'southwest':
        flag = 7;
        break;
        case 'west':
        flag = 8;
        break;
        default:
        flag = 0;
    }
    if(angle >= 360 || angle <= -360) {
        angle = angle % 360;
    }
    if(angle > 45 / 2 && angle <= 135 / 2) {
        flag = flag + 1;
    }
    if(angle < -45/2 && angle >= -135/2) {
        flag = flag - 1;
    }
    if(angle > 135 / 2 && angle <= 225 / 2) {
        flag = flag + 2;
    }
    if(angle < -135 / 2 && angle >= -225 / 2) {
        flag = flag - 2;
    }
    if(angle > 225 / 2 && angle <= 315 / 2) {
        flag = flag + 3;
    }
    if(angle < -225 / 2 && angle >= -315 / 2) {
        flag = flag - 3;
    }
    if(angle > 315 / 2 && angle <= 405 / 2) {
        flag = flag + 4;
    }
    if(angle < -315 / 2 && angle >= -405 / 2) {
        flag = flag - 4;
    }
    if(angle > 405 / 2 && angle <= 495 / 2) {
        flag = flag + 5;
    }
    if(angle < -405 / 2 && angle >= -495 / 2) {
        flag = flag - 5;
    }
    if(angle > 495 / 2 && angle <= 585 / 2) {
        flag = flag + 6;
    }
    if(angle < -495 / 2 && angle >= -585 / 2) {
        flag = flag - 6;
    }
    if(angle > 585 / 2 && angle <= 675 / 2) {
        flag = flag + 7;
    }
    if(angle < -585 / 2 && angle >= -675 / 2) {
        flag = flag - 7;
    }
    if(flag > 8) {
        flag = flag - 8;
    }
    if(flag <= 0) {
        flag = flag + 8;
    }
    return cursorArray[flag];
}
