        if (size < 0) {
            return;
        }
        var offset = size + outlineWidth;
        $math.grow(rect, offset, offset);
        // this._viewRect = $math.unionRect(rect, this._viewRect);

        if (isResizePoints == true) {
            this.resizingPoints = points;
        } else {
            this.editPoints = points;
        }
