		var unionBounds = this.network._unionBounds;
		var rx = unionBounds.x;
		var ry = unionBounds.y;
		var rw = unionBounds.width;
		var rh = unionBounds.height;
		var right = realSize.width;
		var bottom = realSize.height;

		var width = w;
		var height = h;

